import { Component } from "react";
import { Statistics } from "./Statistics/Statistics";
import { FeedbackOptions } from "./FeedbackOptions/FeedbackOptions";
import css from './feedback.module.css';

export class Feedback extends Component {
  state = {
  good: 0,
  neutral: 0,
  bad: 0,
//   total: 0,
//   positivePercentage: 0,
    }

    incrementButton = (e) => {
        // console.log(e.target.name);
        this.setState((prevState) => ({
          [e.target.name]: prevState[e.target.name]+1,
        }));
        // this.countTotalFeedback();
        this.countPositiveFeedbackPercentage();
    }

    countTotalFeedback=()=>{
        return (this.state.good + this.state.neutral + this.state.bad)
    }

    countPositiveFeedbackPercentage=()=>{
        return Math.round(this.state.good/(this.countTotalFeedback())*100)
    }
    render() {

        return (
            <>
    <h2>Please leave feedback</h2> 
    <FeedbackOptions options={1} onLeaveFeedback={this.incrementButton()}></FeedbackOptions>           
        {/* <div className="buttonsContainer">
            <button type='button' name = 'good' onClick={this.incrementButton}>Good</button>
            <button type='button' name = 'neutral' onClick={this.incrementButton}>Neutral</button>
            <button type='button' name = 'bad' onClick={this.incrementButton}>Bad</button>
                </div> */}
    <h2>Statistics</h2>
    <Statistics good={this.state.good} neutral={this.state.neutral} bad={this.state.bad} total={this.countTotalFeedback()} positivePercentage={this.countPositiveFeedbackPercentage()}/>

        </>)   
  }
}
